package com.leonardo.gomes.pojo;

public class RulingPojo {
	String date;
	String text;
	public RulingPojo() {
		super();
		// TODO Auto-generated constructor stub
	}
	public RulingPojo(String date, String text) {
		super();
		this.date = date;
		this.text = text;
	}
	public String getDate() {
		return date;
	}
	public void setDate(String date) {
		this.date = date;
	}
	public String getText() {
		return text;
	}
	public void setText(String text) {
		this.text = text;
	}
}
